args:
& {
$ErrorActionPreference='Stop';
$root='${workspaceFolder}';
$public=Join-Path $root 'public';
$snapRoot=Join-Path $root 'snapshots';
if (!(Test-Path $public)) { throw 'public/ not found. Build or copy first.' }
if (!(Test-Path $snapRoot)) { New-Item -ItemType Directory -Force -Path $snapRoot | Out-Null }
$ts=Get-Date -Format 'yyyy-MM-dd-HHmmss';
$dest=Join-Path $snapRoot $ts;
robocopy $public $dest /MIR /R:1 /W:1 /NFL /NDL /NJH /NJS /NP | Out-Null;
$code=$LASTEXITCODE; if ($code -ge 8) { throw "robocopy failed ($code)" }
& git add -- $dest;
& git commit -m "snapshot: $ts (public -> snapshots/$ts)";
Write-Host "SNAPSHOT_OK: $dest"; }

& {
$ErrorActionPreference='Stop';
$root='${workspaceFolder}';
$src=Join-Path $root 'src';
$public=Join-Path $root 'public';
$deploy=Join-Path $root 'santino.ru.com';
$snapRoot=Join-Path $root 'snapshots';
@($public,$deploy,$snapRoot) | % { if (!(Test-Path $_)) { New-Item -ItemType Directory -Force -Path $_ | Out-Null } }
robocopy $src $public /MIR /R:1 /W:1 /NFL /NDL /NJH /NJS /NP | Out-Null; if ($LASTEXITCODE -ge 8) { throw 'robocopy src->public failed' }
robocopy $public $deploy /MIR /R:1 /W:1 /NFL /NDL /NJH /NJS /NP | Out-Null; if ($LASTEXITCODE -ge 8) { throw 'robocopy public->deploy failed' }
$ts=Get-Date -Format 'yyyy-MM-dd-HHmmss';
$dest=Join-Path $snapRoot $ts;
robocopy $public $dest /MIR /R:1 /W:1 /NFL /NDL /NJH /NJS /NP | Out-Null; if ($LASTEXITCODE -ge 8) { throw 'robocopy public->snapshot failed' }
& git add -- $dest;
& git commit -m "deploy+snapshot: $ts";
Write-Host "DEPLOY_OK: $deploy"; Write-Host "SNAPSHOT_OK: $dest"; }
